import { response } from "msw";
import { useContext } from "react";
import { useEffect , useState , useReducer } from "react";
import { createContext } from "react";


const initialState = {theme: "", element: []}

const ContextGlobal = createContext();

const reducer = (state, action) => {
  switch (action.type) {
    case 'ELEMENT':
      return { ...state, element: action.payload };
    case 'THEME':
      return { ...state, theme: action.payload };
    default:
      return state;
  }
};





export const ContextProvider = ({ children }) => {
  //Aqui deberan implementar la logica propia del Context, utilizando el hook useMemo

  //const [favs, setFavs] = useState([]);
  const [state, dispatch] = useReducer(reducer, initialState);

 
  useEffect(() => {
    async function fetchDataAsync() {
      const response = await fetch('https://jsonplaceholder.typicode.com/users%27');
      const info = await response.json();
      dispatch({ type: 'ELEMENT', payload: info });
    }
    fetchDataAsync();
  }, []);

  //const handleThemeChange = () => {
  //  dispatch({ type: 'SET_THEME', payload: state.theme === 'dark' ? '' : 'dark' });
  // };



  return (
    <ContextGlobal.Provider value={{state}}>
      {children}
    </ContextGlobal.Provider>
  );
};

export const useContextGlobal = () => useContext(ContextGlobal);
