  const {state} = useContextGlobal();

  const addFav = ()=>{
    // Aqui iria la logica para agregar la Card en el localStorage
  }

  return (
    <div className="card" >


         <img src="/images/doctor.jpg" alt="Doctor" />
         <h2 ><Link to={`/${id}`}>{name} style={{color:state.theme.color}}</Link></h2>
         <h3 >{username}</h3>
         <button onClick={addFav} className="favButton">Favoritos</button>
    
    </div>
  );
};

