  return (
    <div className="card" >


         <img src="/images/doctor.jpg" alt="Doctor" />
         <h2 style={{color:state.theme.color}}><Link to={`/${id}`}>{name}</Link></h2>
         <h3>{username}</h3>
         <button onClick={addFav} className="favButton">Favoritos</button>
    
    </div>
  );
};

